$(document).ready(function () {
    $.ajaxSetup({ cache: false });
    $(".btnInfo").click(function () {
        
        var id = $(this).data("value");

        $("#details").load("/Filmes/Detalhes/" + id, function () {
            $('#myModal').modal("show");
        });
    });
});

